import { Card, Col, Form, List,  Table, Space, Select, Typography, Image, Tag, Button,Tabs,Divider } from 'antd';
import React, { useEffect, useState } from 'react';
import { connect, history } from 'umi';
import moment from 'moment';

import ChatforAccessService from '@/components/ChatForAccessService';
import SubSelection from './components/SubSelection';
import StandardFormRow from './components/StandardFormRow';
import styles from './style.less';

const { Option } = Select;
const FormItem = Form.Item;
const { Paragraph } = Typography;

const { TabPane } = Tabs;

const ResourceNew = ({ dispatch, listAndsearchAndResourceNew: { list = [], total = 0,data={} }, loading }) => {
  const [chatLog, setChatLog] = useState(false);
  const [keys, setKeys] = useState("equipment");
  const goToDetail = (productID,type) => {
    
    history.push({
      pathname: '/details_'+type,
      query: {
        productID,
      },
    });
  };
  
  
  const [currentPageSize, setCurrentPageSize] = useState(10);
  const [currentGlue, setCurrentGlue] = useState(4);
  const expert_render= (item) => (
    <List.Item>
      <Card
        className={styles.card}
        hoverable
        onClick={() => goToDetail(item.id,"experts")}
        cover={
          <Image
            height={150}
            alt={item.title}
            src={item.picpath}
            fallback="data:image/png;base64,"
          />
        }
      >
        <Card.Meta
          title={item.name}
          description={
            
              <span>职位：{item.workunit} </span>
           
          }
        />
        <div className={styles.cardItemContent}>
        {item.specialty}  
        {/* <span>创办日期：{moment(item.approvaldate).format('LL')}</span> */}
        </div>
      </Card>
    </List.Item>
  )
  const company_list = (item) => (
    <List.Item>
      <Card
        className={styles.card}
        hoverable
        onClick={() => goToDetail(item.id,"company")}
        cover={
          <Image
            height={100}
            alt={item.title}
            src={item.url}
            fallback="data:image/png;base64,"
          />
        }
      >
        <Card.Meta
          title={item.name}
          description={
            <Paragraph
              className={styles.item}
            >
              {/* {item.address}
              <br /> */}
              
              产业：{item.industry}
              <br/>
              注册资本：{item.registercapital}
            </Paragraph>
          }
        />
        <div className={styles.cardItemContent}>
        <span>创办日期：{moment(item.approvaldate).format('LL')}</span>
        </div>
      </Card>
    </List.Item>
  )
  const render_list = (item) =>(
    <List.Item>
      <Card
        className={styles.card}
        hoverable
        onClick={() => goToDetail(item.id,'resource')}
        cover={
          <Image
            height={300}
            alt={item.title}
            src={item.imgLink}
            fallback="data:image/png;base64,"
          />
        }
      >
        <Card.Meta
          title={item.name}
          description={
            <Paragraph
              className={styles.item}
              ellipsis={{
                rows: 2,
              }}
            >
              {item.company}
              <br />
              <span>购买日期：{moment(item.buydate).format('LL')}</span>
            </Paragraph>
          }
        />
        <div className={styles.cardItemContent}>
          <Tag color="blue">{item.viewInfo}</Tag>
          <Tag color="green">地点：{item.place}</Tag>
        </div>
      </Card>
    </List.Item>
  )
  const render_fun = {'equipment': render_list,'expert':expert_render,'company':company_list}
  useEffect(() => {
    
    dispatch({
      type: 'listAndsearchAndResourceNew/'+keys,
      payload: {
        current: 0,
        limit: currentPageSize,
      },
    });
  }, [keys]);
  
  
  return (
    <div className={styles.coverCardList}>
      <Card bordered={false}>
        <Form
          layout="inline"
          onValuesChange={({category}) => {
            // console.log(value)
            // 表单项变化时请求数据
            // 模拟查询表单生效
            if(category!=null&&category.length>0){
              category.map((item)=>{
              switch(item){
                case '仪器设备':
                {
                  setKeys('equipment')
                  setCurrentPageSize(10)
                  setCurrentGlue(4)

                  break;
                }
                case '专利检索':{
                  break;
                }case '检验检测':{
                  setKeys('company')
                  setCurrentPageSize(10)
                  setCurrentGlue(4)

                  break;
                }
                case '科技人才':
                  {
                    setKeys('expert')
                    setCurrentPageSize(10)
                    setCurrentGlue(6)
                    break;
                  }
                default:{
                  console.log("未匹配对应内容")
                }
              }
           })
            }
          
            dispatch({
              type: 'listAndsearchAndResourceNew/'+keys,
              payload: {
                current: 0,
                limit: currentPageSize,
                category
              },
            });
          }}
        >
          <SubSelection  /> 
        </Form>
      </Card>
      <div className={styles.cardList}>
        
          {/* {type_in === 'equipment' ?cardList:()=>{setKeys('expert');return expertList;}} */}
       {list&&(<List
      rowKey="id"
      loading={loading}
      grid={{
        gutter: 16,
        xs: 1,
        sm: 2,
        md: 3,
        lg: 3,
        xl: 5,
        xxl: 5,
      }}
      pagination={{
        onChange: (page, pageSize) => {
          // console.log(pageSize)
          setCurrentPageSize(pageSize);
          dispatch({
            type: 'listAndsearchAndResourceNew/'+keys,
            payload: {
              current: page,
              limit: pageSize,
            },
          });
        },
        pageSize: currentPageSize,
        total,pageSizeOptions:[10,16,24]
      }}
      dataSource={list}
      renderItem={render_fun[keys]}
    />)}
          {/* {companyList}
          
          {expertList}
          
          {petentList} */}
         
      </div>
      <div
        style={{
          zIndex: '999999',
          right: '38px',
          bottom: '49px',
          position: 'fixed',
          display: 'block',
        }}
      >
        
          <div style={{display: chatLog ? 'block' : 'none',
            position: 'absolute',
            overflow: 'hidden',
            left: '-333px',
            bottom: '45px', height: '554px', width: '460px',
            borderRadius:"20px",
            boxShadow:"4px 4px 5px 3px #999",
            }}>
            <ChatforAccessService />
          </div>
       
        <Button
          style={{ left: 30 }}
          type="primary"
          onClick={() => (chatLog ? setChatLog(false) : setChatLog(true))}
        >
          询问智能客服
        </Button>
      </div>
    </div>
  );
};

export default connect(({ listAndsearchAndResourceNew, loading }) => ({
  listAndsearchAndResourceNew,
  loading: loading.models.listAndsearchAndResourceNew,
}))(ResourceNew);
