import { Card, Col, Form, List, Row, Select, Typography, Image, Tag, Button } from 'antd';
import React, { useEffect, useState } from 'react';
import { connect, history } from 'umi';
import moment from 'moment';

import ChatforAccessService from '@/components/ChatForAccessService';
import SubSelection from './components/SubSelection';
import StandardFormRow from './components/StandardFormRow';
import styles from './style.less';

const { Option } = Select;
const FormItem = Form.Item;
const { Paragraph } = Typography;


const ServiceList = ({ dispatch, listAndservice: { list = [], total = 0 }, loading }) => {
  const [chatLog, setChatLog] = useState(false);
  const goToDetail = (productID) => {
    history.push({
      pathname: 'service_list/detail',
      query: {
        productID,
      },
    });
  };
  const [currentPageSize, setCurrentPageSize] = useState(8);
  useEffect(() => {
    dispatch({
      type: 'listAndservice/list',
      payload: {
        current: 0,
        limit: 8,
      },
    });
    console.log(list);
  }, []);

  const cardList = list && (
    <List
      itemLayout="vertical"
      rowKey="id"
      loading={loading}
      grid={{
        gutter: 16,
        xs: 1,
        sm: 2,
        md: 3,
        lg: 3,
        xl: 4,
        xxl: 4,
      }}
      pagination={{
        onChange: (page, pageSize) => {
          // console.log(pageSize)
          setCurrentPageSize(pageSize);
          dispatch({
            type: 'listAndservice/list',
            payload: {
              current: page,
              limit: pageSize,
            },
          });
        },
        pageSize: currentPageSize,
        total,
      }}
      dataSource={list}
      renderItem={(item) => (
        <List.Item>
          <Card
            className={styles.card}
            hoverable
            onClick={() => goToDetail(item.id)}
            cover={
              <Image
                // height={200}
                alt={item.serviceName}
                src={item.servicePicture}
                fallback="data:image/png;base64,"
              />
            }
          >
            <Card.Meta
              title={item.serviceName}
              description={
                <Paragraph
                  className={styles.item}
                  ellipsis={{
                    rows: 3,
                  }}
                >
                  {item.serviceProvider}
                  <br />
                  <span>价格：{item.servicePrice} 注册日期：{moment(item.createTime).format('y-M-d')}</span>
                </Paragraph>
              }
            />
            <div className={styles.cardItemContent}>
              <Tag color="blue">{item.url}</Tag>
              <Tag color="green">地点：{item.registrationAddress}</Tag>
            </div>
          </Card>

        </List.Item>
      )}
    />
  );
  const formItemLayout = {
    wrapperCol: {
      xs: {
        span: 24,
      },
      sm: {
        span: 16,
      },
    },
  };
  return (
    <div className={styles.coverCardList}>
      <Card bordered={false}>
        <Form
          layout="inline"
          onValuesChange={() => {
            // console.log(data)
            // 表单项变化时请求数据
            // 模拟查询表单生效
            dispatch({
              type: 'listAndservice/list',
              payload: {
                current: 0,
                limit: 8,
              },
            });
          }}
        >
          {/* <StandardFormRow
            title="所属类目"
            block
            style={{
              paddingBottom: 11,
            }}
          >
            <FormItem name="category">
              <TagSelect expandable onChange={(checkedTags)=>{console.log(checkedTags)}}>
                <TagSelect.Option  value="类目一">类目一</TagSelect.Option>
                <TagSelect.Option  value="类目二">类目二</TagSelect.Option>
                <TagSelect.Option  value="类目三">类目三</TagSelect.Option>
              </TagSelect>
            </FormItem>
          </StandardFormRow> */}
          <SubSelection />
          <StandardFormRow title="其它选项" grid last>
            <Row gutter={16}>
              <Col lg={8} md={10} sm={10} xs={24}>
                <FormItem {...formItemLayout} label="地点" name="place">
                  <Select
                    placeholder="不限"
                    style={{
                      maxWidth: 200,
                      width: '100%',
                    }}
                  >
                    <Option value="lisa">王昭君</Option>
                  </Select>
                </FormItem>
              </Col>
              <Col lg={8} md={10} sm={10} xs={24}>
                <FormItem {...formItemLayout} label="好评度" name="rate">
                  <Select
                    placeholder="不限"
                    style={{
                      maxWidth: 200,
                      width: '100%',
                    }}
                  >
                    <Option value="good">优秀</Option>
                    <Option value="normal">普通</Option>
                  </Select>
                </FormItem>
              </Col>
            </Row>
          </StandardFormRow>
        </Form>
      </Card>
      <div className={styles.cardList}>{cardList}</div>
      <div
        style={{
          zIndex: '999999',
          right: '38px',
          bottom: '49px',
          position: 'fixed',
          display: 'block',
        }}
      >

        <div style={{display: chatLog ? 'block' : 'none',
          position: 'absolute',
          overflow: 'hidden',
          left: '-333px',
          bottom: '45px', height: '554px', width: '460px',
          borderRadius:"20px",
          boxShadow:"4px 4px 5px 3px #999",
        }}>
          <ChatforAccessService />
        </div>

        <Button
          style={{ left: 30 }}
          type="primary"
          onClick={() => (chatLog ? setChatLog(false) : setChatLog(true))}
        >
          询问智能客服
        </Button>
      </div>
    </div>
  );
};

export default connect(({ listAndservice, loading }) => ({
  listAndservice,
  loading: loading.models.listAndservice,
}))(ServiceList);
